#pragma once

#include <stdint.h>
#include <vbe.h>

// Basic color type for 32-bit RGBA
typedef struct {
    uint8_t b, g, r, a;  // BGRA format (common in framebuffers)
} color_t;

// Basic GUI context
typedef struct {
    uint32_t *framebuffer;  // Virtual address of framebuffer
    uint32_t width;
    uint32_t height;
    uint32_t pitch;         // Bytes per row
    uint8_t bpp;            // Bits per pixel
    uint8_t bytes_per_pixel;
    bool initialized;
} gui_context_t;

// Color constants
#define COLOR_BLACK   ((color_t){0, 0, 0, 255})
#define COLOR_WHITE   ((color_t){255, 255, 255, 255})
#define COLOR_RED     ((color_t){0, 0, 255, 255})
#define COLOR_GREEN   ((color_t){0, 255, 0, 255})
#define COLOR_BLUE    ((color_t){255, 0, 0, 255})
#define COLOR_GRAY    ((color_t){128, 128, 128, 255})

// Function declarations
int gui_init(void);
void gui_clear_screen(color_t color);
void gui_put_pixel(int x, int y, color_t color);
void gui_draw_rect(int x, int y, int width, int height, color_t color);
void gui_draw_filled_rect(int x, int y, int width, int height, color_t color);
uint32_t color_to_pixel(color_t color);
gui_context_t* gui_get_context(void);
